abstract contract BaseRegistrar is Ownable, IERC721 {
    uint constant public GRACE_PERIOD = 90 days;

    event ControllerAdded(address indexed controller);
    event ControllerRemoved(address indexed controller);
    event NameMigrated(uint256 indexed id, address indexed owner, uint expires);
    event NameRegistered(uint256 indexed id, address indexed owner, uint expires);
    event NameRenewed(uint256 indexed id, uint expires);

    // The PNS registry
    PNS public pns;

    // The namehash of the TLD this registrar owns (eg, .eth)
    bytes32 public baseNode;

    // A map of addresses that are authorised to register and renew names.
    mapping(address => bool) public controllers;

    // Authorises a controller, who can register and renew domains.
    function addController(address controller) virtual external;

    // Revoke controller permission for an address.
    function removeController(address controller) virtual external;

    // Set the resolver for the TLD this registrar manages.
    function setResolver(address resolver) virtual external;

    // Returns the expiration timestamp of the specified label hash.
    function nameExpires(uint256 id) virtual external view returns (uint);

    // Returns true iff the specified name is available for registration.
    function available(uint256 id) virtual public view returns (bool);

    /**
     * @dev Register a name.
     */
    function register(uint256 id, address owner, uint duration) virtual external returns (uint);

    function renew(uint256 id, uint duration) virtual external returns (uint);

    /**
     * @dev Reclaim ownership of a name in PNS, if you own it in the registrar.
     */
    function reclaim(uint256 id, address owner) virtual external;
}
","keccak256":"0x3de984337588d6da8e0045b0161816bcd10b8103f0888d5646e32048e4750136"},"contracts/registrar/BaseRegistrarImplementation.sol":{"content":"pragma solidity >=0.8.4;

import "../registry/PNS.sol";
import "../resolvers/Resolver.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "./BaseRegistrar.sol";
import "./StringUtils.sol";
import "./BaseRegistrar.sol";


contract BaseRegistrarImplementation is ERC721, ERC721Enumerable, BaseRegistrar {
    // A map of expiry times
    mapping(uint256 => uint) expiries;

    bytes4 constant private INTERFACE_META_ID = bytes4(keccak256("supportsInterface(bytes4)"));
    bytes4 constant private ERC721_ID = bytes4(
        keccak256("balanceOf(address)") ^
        keccak256("ownerOf(uint256)") ^
        keccak256("approve(address,uint256)") ^
        keccak256("getApproved(uint256)") ^
        keccak256("setApprovalForAll(address,bool)") ^
        keccak256("isApprovedForAll(address,address)") ^
        keccak256("transferFrom(address,address,uint256)") ^
        keccak256("safeTransferFrom(address,address,uint256)") ^
        keccak256("safeTransferFrom(address,address,uint256,bytes)")
    );
    bytes4 constant private RECLAIM_ID = bytes4(keccak256("reclaim(uint256,address)"));
    string public baseUri;
    string public baseName;

    /**
     * v2.1.3 version of _isApprovedOrOwner which calls ownerOf(tokenId) and takes grace period into consideration instead of ERC721.ownerOf(tokenId);
     * https://github.com/OpenZeppelin/openzeppelin-contracts/blob/v2.1.3/contracts/token/ERC721/ERC721.sol#L187
     * @dev Returns whether the given spender can transfer a given token ID
     * @param spender address of the spender to query
     * @param tokenId uint256 ID of the token to be transferred
     * @return bool whether the msg.sender is approved for the given token ID,
     *    is an operator of the owner, or is the owner of the token
     */
    function _isApprovedOrOwner(address spender, uint256 tokenId) internal view override returns (bool) {
        address owner = ownerOf(tokenId);
        return (spender == owner || getApproved(tokenId) == spender || isApprovedForAll(owner, spender));
    }

    constructor(string memory _name, string memory _symbol, PNS _pns, bytes32 _baseNode, string memory _baseName) ERC721(_name, _symbol) {
        pns = _pns;
        baseNode = _baseNode;
        baseName = _baseName;
    }

    modifier live {
        require(pns.owner(baseNode) == address(this));
        _;
    }

    modifier onlyController {
        require(controllers[msg.sender],"ERC721: caller is not Controller");
        _;
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal override(ERC721, ERC721Enumerable) {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    /**
     * @dev See {IERC721-transferFrom}.
     */
    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public onlyController override(ERC721, IERC721) {
        require(
            _isApprovedOrOwner(_msgSender(), tokenId),
            "ERC721: caller is not approved or owner"
        );

        _transfer(from, to, tokenId);
        pns.setSubnodeOwner(baseNode, bytes32(tokenId), to);
    }

    /**
     * @dev See {IERC721-safeTransferFrom}.
     */
    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        bytes memory _data
    ) public onlyController override(ERC721, IERC721) {
        require(
            _isApprovedOrOwner(_msgSender(), tokenId),
            "ERC721: caller is not approved or owner"
        );
        _safeTransfer(from, to, tokenId, _data);
        pns.setSubnodeOwner(baseNode, bytes32(tokenId), to);
    }

    /**
     * @dev Gets the owner of the specified token ID. Names become unowned
     *      when their registration expires.
     * @param tokenId uint256 ID of the token to query the owner of
     * @return address currently marked as the owner of the given token ID
     */
    function ownerOf(uint256 tokenId) public view override(IERC721, ERC721) returns (address) {
        require(expiries[tokenId] > block.timestamp);
        return super.ownerOf(tokenId);
    }

    // Authorises a controller, who can register and renew domains.
    function addController(address controller) external override onlyOwner {
        controllers[controller] = true;
        emit ControllerAdded(controller);
    }

    // Revoke controller permission for an address.
    function removeController(address controller) external override onlyOwner {
        controllers[controller] = false;
        emit ControllerRemoved(controller);
    }

    // Set the resolver for the TLD this registrar manages.
    function setResolver(address resolver) external override onlyOwner {
        pns.setResolver(baseNode, resolver);
    }

    // Returns the expiration timestamp of the specified id.
    function nameExpires(uint256 id) external view override returns (uint) {
        return expiries[id];
    }

    // Returns true iff the specified name is available for registration.
    function available(uint256 id) public view override returns (bool) {
        // Not available if it's registered here or in its grace period.
        return expiries[id] + GRACE_PERIOD < block.timestamp;
    }

    /**
     * @dev Register a name.
     * @param id The token ID (keccak256 of the label).
     * @param owner The address that should own the registration.
     * @param duration Duration in seconds for the registration.
     */
    function register(uint256 id, address owner, uint duration) external override returns (uint) {
        return _register(id, owner, duration, true);
    }

    /**
     * @dev Register a name, without modifying the registry.
     * @param id The token ID (keccak256 of the label).
     * @param owner The address that should own the registration.
     * @param duration Duration in seconds for the registration.
     */
    function registerOnly(uint256 id, address owner, uint duration) external returns (uint) {
        return _register(id, owner, duration, false);
    }

    function _register(uint256 id, address owner, uint duration, bool updateRegistry) internal live onlyController returns (uint) {
        require(available(id));
        require(block.timestamp + duration + GRACE_PERIOD > block.timestamp + GRACE_PERIOD); // Prevent future overflow
        expiries[id] = block.timestamp + duration;
        if (_exists(id)) {
            // Name was previously owned, and expired
            _burn(id);
        }
        _mint(owner, id);
        if (updateRegistry) {
            pns.setSubnodeOwner(baseNode, bytes32(id), owner);
        }

        emit NameRegistered(id, owner, block.timestamp + duration);

        return block.timestamp + duration;
    }

    function renew(uint256 id, uint duration) external override live onlyController returns (uint) {
        require(expiries[id] + GRACE_PERIOD >= block.timestamp); // Name must be registered here or in grace period
        require(expiries[id] + duration + GRACE_PERIOD > duration + GRACE_PERIOD); // Prevent future overflow

        expiries[id] += duration;
        emit NameRenewed(id, expiries[id]);
        return expiries[id];
    }

    /**
     * @dev Reclaim ownership of a name in PNS, if you own it in the registrar.
     */
    function reclaim(uint256 id, address owner) external override live {
        require(_isApprovedOrOwner(msg.sender, id));
        pns.setSubnodeOwner(baseNode, bytes32(id), owner);
    }

    function supportsInterface(bytes4 interfaceID) public override(ERC721, IERC165, ERC721Enumerable) view returns (bool) {
        return interfaceID == INTERFACE_META_ID || interfaceID == ERC721_ID || interfaceID == RECLAIM_ID;
    }

    /**
     * PRIVILEGED MODULE FUNCTION. Sets a new baseURI for all token types.
     */
    function setURI(string memory newURI) external onlyOwner {
        baseUri = newURI;
    }

    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");
        string memory baseURI = baseUri;
        return bytes(baseURI).length > 0 ? string(abi.encodePacked(baseURI, Strings.toString(tokenId))) : "";
    }

    function getName(uint256 tokenId) public view returns (string memory) {
        bytes32 label = bytes32(tokenId);
        bytes32 nameHash = keccak256(abi.encodePacked(baseNode, label));
        Resolver resolver = Resolver(pns.resolver(nameHash));
        require(address(resolver) != address(0), "resolver not found");
        string memory theName = resolver.name(nameHash);
        return theName;
    }
}","keccak256":"0x06451b12be53e74fcb8fe6abfd35a65635d13ea27daab72afc6e51d0cb28ca00"},"contracts/registrar/RegistrarControllerV1.sol":{"content":"pragma solidity >=0.8.12;

import "./BaseRegistrarImplementation.sol";
import "./StringUtils.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Enumerable.sol";

/**
 * @dev A registrar controller for registering and renewing names at fixed cost.
 */
contract RegistrarControllerV1 is Ownable {
    using StringUtils for *;
    uint256 public constant MIN_REGISTRATION_DURATION = 365 days;
    BaseRegistrarImplementation base;
    constructor (
        BaseRegistrarImplementation _base,
        uint _basePrice
    ) {
        base = _base;
        basePrice = _basePrice;
    }

    uint public basePrice;

    event NameRegistered(
        string name,
        bytes32 indexed label,
        address indexed owner,
        uint256 cost,
        uint256 expires
    );
    event NameRenewed(
        string name,
        bytes32 indexed label,
        uint256 cost,
        uint256 expires
    );

    bytes32 public root;

    function register(
        string memory name,
        address owner,
        uint256 duration,
        address resolver
    ) public payable onlyOwner {
        bytes32 label = keccak256(bytes(name));
        uint256 tokenId = uint256(label);
        uint256 cost = rentPrice(name, duration);
        _registerSingleDomain(name, owner, duration, resolver, tokenId, label, cost, base);
        // Refund any extra payment
        if (msg.value > cost) {
            payable(msg.sender).transfer(msg.value - cost);
        }
    }

    function _registerSingleDomain(
        string memory name,
        address owner,
        uint256 duration,
        address resolver,
        uint tokenId,
        bytes32 label,
        uint cost,
        BaseRegistrarImplementation registrar
    ) internal {
        // The nodehash of this label
        bytes32 nodehash = keccak256(abi.encodePacked(registrar.baseNode(), label));

        // Set this contract as the (temporary) owner, giving it
        uint256 expires;
        // permission to set up the resolver.
        expires = registrar.register(tokenId, address(this), duration);

        // Set the resolver
        registrar.pns().setResolver(nodehash, resolver);

        // Configure the resolver
        Resolver(resolver).setAddr(nodehash, owner);
        Resolver(resolver).setName(nodehash, name);

        // Now transfer full ownership to the expeceted owner
        registrar.reclaim(tokenId, owner);
        registrar.transferFrom(address(this), owner, tokenId);
        emit NameRegistered(name, label, owner, cost, expires);
    }

    function renew(
        string calldata name,
        uint256 duration
    ) external payable {
        uint256 cost = rentPrice(name, duration);
        require(msg.value >= cost);

        bytes32 label = keccak256(bytes(name));
        uint256 expires = base.renew(uint256(label), duration);
        emit NameRenewed(name, label, cost, expires);

        if (msg.value > cost) {
            payable(msg.sender).transfer(msg.value - cost);
        }
    }

    function withdraw() public onlyOwner {
        payable(msg.sender).transfer(address(this).balance);
    }

    function setBasePrice(uint _price) public onlyOwner {
        basePrice = _price;
    }

    function rentPrice(
        string memory name,
        uint256 duration
    ) public view returns (uint256){
        // no need to check basePrice
        // require(basePrice > 0, "configuration incorrect");
        uint price = basePrice * duration;
        return price;
    }

    function valid(string memory name) public view returns (bool) {
        if (name.strlen() < 1) {
            return false;
        }
        bytes memory nb = bytes(name);
        // zero width for /u200b /u200c /u200d and U+FEFF

        for (uint256 i; i < nb.length; i++) {
            bytes1 char = nb[i];
            if (char >= 0x41 && char <= 0x5A) {
                return false; //A-Z
            }
        }
        // zero width
        if (nb.length >= 2) {
            for (uint256 i; i < nb.length - 2; i++) {
                if (bytes1(nb[i]) == 0xe2 && bytes1(nb[i + 1]) == 0x80) {
                    if (
                        bytes1(nb[i + 2]) == 0x8b ||
                        bytes1(nb[i + 2]) == 0x8c ||
                        bytes1(nb[i + 2]) == 0x8d
                    ) {
                        return false;
                    }
                } else if (bytes1(nb[i]) == 0xef) {
                    if (bytes1(nb[i + 1]) == 0xbb && bytes1(nb[i + 2]) == 0xbf)
                        return false;
                }
            }
        }
        return true;
    }

    function available(string memory name) public view returns (bool) {
        bytes32 label = keccak256(bytes(name));
        bool _available = true;
        if (!base.available(uint256(label))) {
            _available = false;
        }
        return valid(name) && _available;
    }

    function withdraw(address receiver) public onlyOwner {
        payable(receiver).transfer(address(this).balance);
    }
}
