contracts/registrar/RegistrarController..content:
pragma solidity >=0.8.12;

import "./BaseRegistrarImplementation.sol";
import "./StringUtils.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./Whitelist.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Enumerable.sol";

/**
 * @dev A registrar controller for registering and renewing names at fixed cost.
 */
contract RegistrarController is Ownable {
    using StringUtils for *;

    uint256 public constant MIN_REGISTRATION_DURATION = 365 days;

    BaseRegistrarImplementation base;
    uint256 public mintPerWallet = 100;
    mapping(address => uint256) public mintCount;
    constructor (
        BaseRegistrarImplementation _base,
        uint _basePrice,
        uint256 _minCommitmentAge,
        uint256 _maxCommitmentAge
    ) {
        base = _base;
        basePrice = _basePrice;
        minCommitmentAge = _minCommitmentAge;
        maxCommitmentAge = _maxCommitmentAge;
    }

    uint public basePrice;

    uint256 public minCommitmentAge;
    uint256 public maxCommitmentAge;
    mapping(bytes32 => uint256) public commitments;

    event NameRegistered(
        string name,
        bytes32 indexed label,
        address indexed owner,
        uint256 cost,
        uint256 expires
    );
    event NameRenewed(
        string name,
        bytes32 indexed label,
        uint256 cost,
        uint256 expires
    );

    bytes32 public root;

    function setMintPerWallet(uint256 _mintPerWallet) public onlyOwner {
        mintPerWallet = _mintPerWallet;
    }

    function makeCommitment(
        string memory name,
        address owner,
        bytes32 secret,
        address resolver
    ) public pure returns (bytes32) {
        bytes32 label = keccak256(bytes(name));
        require(resolver != address(0), "resolver invalid");
        return
        keccak256(
            abi.encodePacked(label, owner, resolver, secret)
        );
    }

    function commit(bytes32 commitment) public {
        require(commitments[commitment] + maxCommitmentAge < block.timestamp);
        commitments[commitment] = block.timestamp;
    }

    function register(
        string memory name,
        address owner,
        uint256 duration,
        bytes32 secret,
        address resolver
    ) public payable {
        require(mintCount[owner] < mintPerWallet, "exceed max mint");

        bytes32 commitment = makeCommitment(
            name,
            owner,
            secret,
            resolver
        );
        uint256 cost = _consumeCommitment(name, duration, commitment);

        bytes32 label = keccak256(bytes(name));
        uint256 tokenId = uint256(label);

        _registerSingleDomain(
            name, owner, duration, resolver, tokenId,
            label, cost, base
        );

        // Refund any extra payment
        if (msg.value > cost) {
            payable(msg.sender).transfer(msg.value - cost);
        }
        mintCount[owner] += 1;
    }

    function _registerSingleDomain(
        string memory name,
        address owner,
        uint256 duration,
        address resolver,
        uint tokenId,
        bytes32 label,
        uint cost,
        BaseRegistrarImplementation registrar
    ) internal {
        // The nodehash of this label
        bytes32 nodehash = keccak256(abi.encodePacked(registrar.baseNode(), label));

        // Set this contract as the (temporary) owner, giving it
        uint256 expires;
        // permission to set up the resolver.
        expires = registrar.register(tokenId, address(this), duration);

        // Set the resolver
        registrar.pns().setResolver(nodehash, resolver);

        // Configure the resolver
        Resolver(resolver).setAddr(nodehash, owner);
        Resolver(resolver).setName(nodehash, name);

        // Now transfer full ownership to the expeceted owner
        registrar.reclaim(tokenId, owner);
        registrar.transferFrom(address(this), owner, tokenId);
        emit NameRegistered(name, label, owner, cost, expires);
    }

    function renew(
        string calldata name,
        uint256 duration
    ) external payable {
        uint256 cost = rentPrice(name, duration);
        require(msg.value >= cost);

        bytes32 label = keccak256(bytes(name));
        uint256 expires = base.renew(uint256(label), duration);
        emit NameRenewed(name, label, cost, expires);

        if (msg.value > cost) {
            payable(msg.sender).transfer(msg.value - cost);
        }
    }

    function setCommitmentAges(
        uint256 _minCommitmentAge,
        uint256 _maxCommitmentAge
    ) public onlyOwner {
        minCommitmentAge = _minCommitmentAge;
        maxCommitmentAge = _maxCommitmentAge;
    }

    function withdraw() public onlyOwner {
        payable(msg.sender).transfer(address(this).balance);
    }

    function _consumeCommitment(
        string memory name,
        uint256 duration,
        bytes32 commitment
    ) internal returns (uint256) {
        // Require a valid commitment
        require(
            commitments[commitment] + minCommitmentAge <= block.timestamp,
            "invalid with minCommitmentAge"
        );

        // If the commitment is too old, or the name is registered, stop
        require(
            commitments[commitment] + maxCommitmentAge > block.timestamp,
            "invalid with maxCommitmentAge"
        );
        require(available(name), "name not available");

        delete (commitments[commitment]);

        uint256 cost = rentPrice(name, duration);
        require(
            duration >= MIN_REGISTRATION_DURATION,
            "Duration should be greater than 365 days"
        );
        require(msg.value >= cost, "invalid pay");

        return cost;
    }

    function setBasePrice(uint _price) public onlyOwner {
        basePrice = _price;
    }

    function rentPrice(
        string memory name,
        uint256 duration
    ) public view returns (uint256){
        // no need to check basePrice
        // require(basePrice > 0, "configuration incorrect");
        uint price = basePrice * duration;
        return price;
    }

    function valid(string memory name) public view returns (bool) {
        if (name.strlen() < 1) {
            return false;
        }
        bytes memory nb = bytes(name);
        // zero width for /u200b /u200c /u200d and U+FEFF

        for (uint256 i; i < nb.length; i++) {
            bytes1 char = nb[i];
            if (char >= 0x41 && char <= 0x5A) {
                return false; //A-Z
            }
        }
        // zero width
        if (nb.length >= 2) {
            for (uint256 i; i < nb.length - 2; i++) {
                if (bytes1(nb[i]) == 0xe2 && bytes1(nb[i + 1]) == 0x80) {
                    if (
                        bytes1(nb[i + 2]) == 0x8b ||
                        bytes1(nb[i + 2]) == 0x8c ||
                        bytes1(nb[i + 2]) == 0x8d
                    ) {
                        return false;
                    }
                } else if (bytes1(nb[i]) == 0xef) {
                    if (bytes1(nb[i + 1]) == 0xbb && bytes1(nb[i + 2]) == 0xbf)
                        return false;
                }
            }
        }
        return true;
    }

    function available(string memory name) public view returns (bool) {
        bytes32 label = keccak256(bytes(name));
        bool _available = true;
        if (!base.available(uint256(label))) {
            _available = false;
        }
        return valid(name) && _available;
    }

    function withdraw(address receiver) public onlyOwner {
        payable(receiver).transfer(address(this).balance);
    }
}

contracts/registrar/Whitelist.sol.content:
pragma solidity >=0.8.12;

import "@openzeppelin/contracts/access/Ownable.sol";

contract Whitelist is Ownable {
    mapping(address => bool) public whitelist;

    modifier onlyWhitelisted() {
        require(whitelist[msg.sender]);
        _;
    }


    function addAddressToWhitelist(address addr) onlyOwner public returns (bool success) {
        if (!whitelist[addr]) {
            whitelist[addr] = true;
            success = true;
        }
    }


    function addAddressesToWhitelist(address[] calldata addrs) onlyOwner public returns (bool success) {
        for (uint256 i = 0; i < addrs.length; i++) {
            if (addAddressToWhitelist(addrs[i])) {
                success = true;
            }
        }
    }


    function removeAddressFromWhitelist(address addr) onlyOwner public returns (bool success) {
        if (whitelist[addr]) {
            whitelist[addr] = false;
            success = true;
        }
    }


    function removeAddressesFromWhitelist(address[] calldata addrs) onlyOwner public returns (bool success) {
        for (uint256 i = 0; i < addrs.length; i++) {
            if (removeAddressFromWhitelist(addrs[i])) {
                success = true;
            }
        }
    }
}

contracts/root/Root.sol.content:
pragma solidity ^0.8.4;

import "../registry/PNS.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./Controllable.sol";

contract Root is Ownable, Controllable {
    bytes32 private constant ROOT_NODE = bytes32(0);

    bytes4 private constant INTERFACE_META_ID =
    bytes4(keccak256("supportsInterface(bytes4)"));

    event TLDLocked(bytes32 indexed label);

    PNS public pns;
    mapping(bytes32 => bool) public locked;

    constructor(PNS _pns) public {
        pns = _pns;
    }

    function setSubnodeOwner(bytes32 label, address owner) external onlyController {
        require(!locked[label]);
        pns.setSubnodeOwner(ROOT_NODE, label, owner);
    }

    function setResolver(address resolver) external onlyOwner {
        pns.setResolver(ROOT_NODE, resolver);
    }

    function lock(bytes32 label) external onlyOwner {
        emit TLDLocked(label);
        locked[label] = true;
    }

    function supportsInterface(bytes4 interfaceID) external pure returns (bool){
        return interfaceID == INTERFACE_META_ID;
    }
}

contracts/root/Controllable.sol.content:
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/access/Ownable.sol";

contract Controllable is Ownable {
    mapping(address => bool) public controllers;

    event ControllerChanged(address indexed controller, bool enabled);

    modifier onlyController {
        require(
            controllers[msg.sender],
            "Controllable: Caller is not a controller"
        );
        _;
    }

    function setController(address controller, bool enabled) public onlyOwner {
        controllers[controller] = enabled;
        emit ControllerChanged(controller, enabled);
    }
}

contracts/registry/ReverseRegistrar.sol.content:
pragma solidity >=0.8.4;

import "./PNS.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "../root/Controllable.sol";

abstract contract NameResolver {
    function setName(bytes32 node, string memory name) public virtual;
}

bytes32 constant lookup = 0x3031323334353637383961626364656600000000000000000000000000000000;

bytes32 constant ADDR_REVERSE_NODE = 0x91d1777781884d03a6757a803996e38de2a42967fb37eeaca72729271025a9e2;

// namehash('addr.reverse')

contract ReverseRegistrar is Ownable, Controllable {
    PNS public pns;
    NameResolver public defaultResolver;

    event ReverseClaimed(address indexed addr, bytes32 indexed node);

    /**
     * @dev Constructor
     * @param pnsAddr The address of the WPNS registry.
     * @param resolverAddr The address of the default reverse resolver.
     */
    constructor(PNS pnsAddr, NameResolver resolverAddr) {
        pns = pnsAddr;
        defaultResolver = resolverAddr;

        // Assign ownership of the reverse record to our deployer
        ReverseRegistrar oldRegistrar = ReverseRegistrar(
            pns.owner(ADDR_REVERSE_NODE)
        );
        if (address(oldRegistrar) != address(0x0)) {
            oldRegistrar.claim(msg.sender);
        }
    }

    modifier authorised(address addr) {
        require(
            addr == msg.sender ||
            controllers[msg.sender] ||
            pns.isApprovedForAll(addr, msg.sender) ||
            ownsContract(addr),
            "Caller is not a controller or authorised by address or the address itself"
        );
        _;
    }

    /**
     * @dev Transfers ownership of the reverse PNS record associated with the
     *      calling account.
     * @param owner The address to set as the owner of the reverse record in PNS.
     * @return The PNS node hash of the reverse record.
     */
    function claim(address owner) public returns (bytes32) {
        return _claimWithResolver(msg.sender, owner, address(0x0));
    }

    /**
     * @dev Transfers ownership of the reverse PNS record associated with the
     *      calling account.
     * @param addr The reverse record to set
     * @param owner The address to set as the owner of the reverse record in PNS.
     * @return The PNS node hash of the reverse record.
     */
    function claimForAddr(address addr, address owner) public authorised(addr) returns (bytes32){
        return _claimWithResolver(addr, owner, address(0x0));
    }

    /**
     * @dev Transfers ownership of the reverse PNS record associated with the
     *      calling account.
     * @param owner The address to set as the owner of the reverse record in PNS.
     * @param resolver The address of the resolver to set; 0 to leave unchanged.
     * @return The PNS node hash of the reverse record.
     */
    function claimWithResolver(address owner, address resolver) public returns (bytes32){
        return _claimWithResolver(msg.sender, owner, resolver);
    }

    /**
     * @dev Transfers ownership of the reverse PNS record specified with the
     *      address provided
     * @param addr The reverse record to set
     * @param owner The address to set as the owner of the reverse record in PNS.
     * @param resolver The address of the resolver to set; 0 to leave unchanged.
     * @return The PNS node hash of the reverse record.
     */
    function claimWithResolverForAddr(
        address addr,
        address owner,
        address resolver
    ) public authorised(addr) returns (bytes32) {
        return _claimWithResolver(addr, owner, resolver);
    }

    /**
     * @dev Sets the `name()` record for the reverse PNS record associated with
     * the calling account. First updates the resolver to the default reverse
     * resolver if necessary.
     * @param name The name to set for this address.
     * @return The PNS node hash of the reverse record.
     */
    function setName(string memory name) public returns (bytes32) {
        bytes32 node = _claimWithResolver(
            msg.sender,
            address(this),
            address(defaultResolver)
        );
        defaultResolver.setName(node, name);
        return node;
    }

    /**
     * @dev Sets the `name()` record for the reverse PNS record associated with
     * the account provided. First updates the resolver to the default reverse
     * resolver if necessary.
     * Only callable by controllers and authorised users
     * @param addr The reverse record to set
     * @param owner The owner of the reverse node
     * @param name The name to set for this address.
     * @return The PNS node hash of the reverse record.
     */
    function setNameForAddr(
        address addr,
        address owner,
        string memory name
    ) public authorised(addr) returns (bytes32) {
        bytes32 node = _claimWithResolver(
            addr,
            address(this),
            address(defaultResolver)
        );
        defaultResolver.setName(node, name);
        pns.setSubnodeOwner(ADDR_REVERSE_NODE, sha3HexAddress(addr), owner);
        return node;
    }

    /**
     * @dev Returns the node hash for a given account's reverse records.
     * @param addr The address to hash
     * @return The PNS node hash.
     */
    function node(address addr) public pure returns (bytes32) {
        return
        keccak256(
            abi.encodePacked(ADDR_REVERSE_NODE, sha3HexAddress(addr))
        );
    }

    /**
     * @dev An optimised function to compute the sha3 of the lower-case
     *      hexadecimal representation of an Ethereum address.
     * @param addr The address to hash
     * @return ret The SHA3 hash of the lower-case hexadecimal encoding of the
     *         input address.
     */
    function sha3HexAddress(address addr) private pure returns (bytes32 ret) {
        assembly {
            for {
                let i := 40
            } gt(i, 0) {

            } {
                i := sub(i, 1)
                mstore8(i, byte(and(addr, 0xf), lookup))
                addr := div(addr, 0x10)
                i := sub(i, 1)
                mstore8(i, byte(and(addr, 0xf), lookup))
                addr := div(addr, 0x10)
            }

            ret := keccak256(0, 40)
        }
    }

    /* Internal functions */

    function _claimWithResolver(
        address addr,
        address owner,
        address resolver
    ) internal returns (bytes32) {
        bytes32 label = sha3HexAddress(addr);
        bytes32 node = keccak256(abi.encodePacked(ADDR_REVERSE_NODE, label));
        address currentResolver = pns.resolver(node);
        bool shouldUpdateResolver = (resolver != address(0x0) &&
            resolver != currentResolver);
        address newResolver = shouldUpdateResolver ? resolver : currentResolver;

        pns.setSubnodeRecord(ADDR_REVERSE_NODE, label, owner, newResolver, 0);

        emit ReverseClaimed(addr, node);

        return node;
    }

    function ownsContract(address addr) internal view returns (bool) {
        try Ownable(addr).owner() returns (address owner) {
            return owner == msg.sender;
        } catch {
            return false;
        }
    }
}

