contracts/registrar/DummyOracle.sol.content:
pragma solidity >=0.8.4;

contract DummyOracle {
    int value;

    constructor(int _value) public {
        set(_value);
    }

    function set(int _value) public {
        value = _value;
    }

    function latestAnswer() public view returns(int256) {
        return value;
    }
}

contracts/registrar/IRegistrarController.content:
pragma solidity >=0.8.4;

interface IRegistrarController {
    function rentPrice(string memory, uint256)
        external
        returns (uint256);

    function available(string memory) external returns (bool);

    function makeCommitment(
        string memory,
        address,
        bytes32,
        address,
        bool
    ) external returns (bytes32);

    function commit(bytes32) external;

    function register(
        string calldata,
        address,
        uint256,
        bytes32,
        address,
        bool
    ) external payable;

    function renew(string calldata, uint256) external payable;
}

contracts/registrar/TestResolver.sol.content:
pragma solidity >=0.8.4;

/**
 * @dev A test resolver implementation
 */
contract TestResolver {
    mapping(bytes32 => address) addresses;

    constructor() public {
    }

    function supportsInterface(bytes4 interfaceID) public pure returns (bool) {
        return interfaceID == 0x01ffc9a7 || interfaceID == 0x3b3b57de;
    }

    function addr(bytes32 node) public view returns (address) {
        return addresses[node];
    }

    function setAddr(bytes32 node, address addr) public {
        addresses[node] = addr;
    }
}

contracts/root/Ownable.sol.content:
pragma solidity ^0.8.4;

contract Ownable {

    address public owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    modifier onlyOwner {
        require(isOwner(msg.sender));
        _;
    }

    constructor() public {
        owner = msg.sender;
    }

    function transferOwnership(address newOwner) public onlyOwner {
        emit OwnershipTransferred(owner, newOwner);
        owner = newOwner;
    }

    function isOwner(address addr) public view returns (bool) {
        return owner == addr;
    }
}
